import React, {useState} from 'react'
import ReactDOM from 'react-dom';
import LineTo from "react-lineto"
import "../style.css";

export default function Dot(props) {
    const [d1, setd1] = useState(0);
    const [d2, setd2] = useState(0);
    const [d3, setd3] = useState(0);
    const [d4, setd4] = useState(0);
    const [turn, setTurn] = useState(0);
    const [prevClick, setPrevClick] = useState(null);
    function whichDot(dotIndex){
        console.log("Current Click: " + dotIndex);
        console.log("Previous click: " + prevClick);
        setd1(dotIndex-1);
        setd2(dotIndex+1);
        setd3(dotIndex-5);
        setd4(dotIndex+5);
        if(dotIndex%5==0){
            setd2(0);
        }
        if(dotIndex%5==1){
            setd1(0);
        }
        // console.log(d1, d2, d3, d4);
        setPrevClick(dotIndex);
        if(turn==0){
            setTurn(1);
        }
        if(turn==1){
            setTurn(0);
            setPrevClick(null);
        }
    }
    return (
    <>
        <div className="row" id="row-1">
          <div className="col A" id={(d1==1||d2==1||d3==1||d4==1)?"highlight":"row-1-col-1"} onClick={()=>{whichDot(1)}}></div>
          <div className="col B" id={(d1==2||d2==2||d3==2||d4==2)?"highlight":"row-1-col-3"} onClick={()=>{whichDot(2)}}></div>
          <div className="col" id={(d1==3||d2==3||d3==3||d4==3)?"highlight":"row-1-col-3"} onClick={()=>{whichDot(3)}}></div>
          <div className="col" id={(d1==4||d2==4||d3==4||d4==4)?"highlight":"row-1-col-4"} onClick={()=>{whichDot(4)}}></div>
          <div className="col" id={(d1==5||d2==5||d3==5||d4==5)?"highlight":"row-1-col-5"} onClick={()=>{whichDot(5)}}></div>
        </div>
        <div className="row" id="row-2">
          <div className="col" id={(d1==6||d2==6||d3==6||d4==6)?"highlight":"row-2-col-1"} onClick={()=>{whichDot(6)}}></div>
          <div className="col" id={(d1==7||d2==7||d3==7||d4==7)?"highlight":"row-2-col-2"} onClick={()=>{whichDot(7)}}></div>
          <div className="col" id={(d1==8||d2==8||d3==8||d4==8)?"highlight":"row-2-col-3"} onClick={()=>{whichDot(8)}}></div>
          <div className="col" id={(d1==9||d2==9||d3==9||d4==9)?"highlight":"row-2-col-4"} onClick={()=>{whichDot(9)}}></div>
          <div className="col" id={(d1==10||d2==10||d3==10||d4==10)?"highlight":"row-2-col-5"} onClick={()=>{whichDot(10)}}></div>
        </div>
        <div className="row" id="row-3">
          <div className="col" id={(d1==11||d2==11||d3==11||d4==11)?"highlight":"row-3-col-1"} onClick={()=>{whichDot(11)}}></div>
          <div className="col" id={(d1==12||d2==12||d3==12||d4==12)?"highlight":"row-3-col-2"} onClick={()=>{whichDot(12)}}></div>
          <div className="col" id={(d1==13||d2==13||d3==13||d4==13)?"highlight":"row-3-col-3"} onClick={()=>{whichDot(13)}}></div>
          <div className="col" id={(d1==14||d2==14||d3==14||d4==14)?"highlight":"row-3-col-4"} onClick={()=>{whichDot(14)}}></div>
          <div className="col" id={(d1==15||d2==15||d3==15||d4==15)?"highlight":"row-3-col-5"} onClick={()=>{whichDot(15)}}></div>
        </div>
        <div className="row" id="row-4">
          <div className="col" id={(d1==16||d2==16||d3==16||d4==16)?"highlight":"row-4-col-1"} onClick={()=>{whichDot(16)}}></div>
          <div className="col" id={(d1==17||d2==17||d3==17||d4==17)?"highlight":"row-4-col-2"} onClick={()=>{whichDot(17)}}></div>
          <div className="col" id={(d1==18||d2==18||d3==18||d4==18)?"highlight":"row-4-col-3"} onClick={()=>{whichDot(18)}}></div>
          <div className="col" id={(d1==19||d2==19||d3==19||d4==19)?"highlight":"row-4-col-4"} onClick={()=>{whichDot(19)}}></div>
          <div className="col" id={(d1==20||d2==20||d3==20||d4==20)?"highlight":"row-4-col-5"} onClick={()=>{whichDot(20)}}></div>
        </div>
        <div className="row" id="row-5">
          <div className="col" id={(d1==21||d2==21||d3==21||d4==21)?"highlight":"row-5-col-1"} onClick={()=>{whichDot(21)}}></div>
          <div className="col" id={(d1==22||d2==22||d3==22||d4==22)?"highlight":"row-5-col-2"} onClick={()=>{whichDot(22)}}></div>
          <div className="col" id={(d1==23||d2==23||d3==23||d4==23)?"highlight":"row-5-col-3"} onClick={()=>{whichDot(23)}}></div>
          <div className="col" id={(d1==24||d2==24||d3==24||d4==24)?"highlight":"row-5-col-4"} onClick={()=>{whichDot(24)}}></div>
          <div className="col" id={(d1==25||d2==25||d3==25||d4==25)?"highlight":"row-5-col-5"} onClick={()=>{whichDot(25)}}></div>
        </div>
        <div className="row" id="row-6">
          <div className="col" id={(d1==26||d2==26||d3==26||d4==26)?"highlight":"row-6-col-1"} onClick={()=>{whichDot(26)}}></div>
          <div className="col" id={(d1==27||d2==27||d3==27||d4==27)?"highlight":"row-6-col-2"} onClick={()=>{whichDot(27)}}></div>
          <div className="col" id={(d1==28||d2==28||d3==28||d4==28)?"highlight":"row-6-col-3"} onClick={()=>{whichDot(28)}}></div>
          <div className="col" id={(d1==29||d2==29||d3==29||d4==29)?"highlight":"row-6-col-4"} onClick={()=>{whichDot(29)}}></div>
          <div className="col" id={(d1==30||d2==30||d3==30||d4==30)?"highlight":"row-6-col-5"} onClick={()=>{whichDot(30)}}></div>
        </div>
        <LineTo from="A" to="B"/>
    </>
  )
}
