import React from 'react'
import Dot from "./Dot"

export default function Grid() {
    var grid = [];
    var keyCount=0;
    let i, j;

    (function createBoard(){
        for(i=0; i<9; ++i){
            if(i%2==0){
                // horizontal orientation lines
                for(j=0; j<9; ++j){
                    grid.push(<Dot key={keyCount++} orientation={j%2==0?"dot":"horizontal-bar"}/>)
                }
            }
            else{
                for(j=0; j<5; ++j){
                    grid.push(<Dot key={keyCount++} orientation="vertical-bar"/>)
                }
            }
            grid.push(<br/>);
        }
    })()
    return (
        <>
            {/* <div className="horizontal-grid">{horizontalGrid}</div>
            <div className="vertical-grid">{verticalGrid}</div> */}
            <div className="grid">{grid}</div>
        </>
    )
}
