import React, {useState} from 'react'
import Dot from "./Dot";
import "../style.css";

export default function Container(props) {
    // const [map, setMap] = useState(new Map());
    const [arr, setArr] = useState();
    let map = new Map();

    var grid = [];
    var dotCount, horizontalCount, verticalCount;
    var keyCountHorizontal = 0, keyCountVertical = 0;
    let i;
    let rowCount = Math.floor(props.row/2)+1;
    (function createBoard(){
        if(props.row%2==0){
            for(i=0; i<9; ++i){
                dotCount = (keyCountHorizontal/2 + 1);
                horizontalCount = Math.ceil((keyCountHorizontal+1)/2);
                grid.push(<Dot 
                                key={keyCountHorizontal++} 
                                elementName={i%2==0?"dot":"horizontal-bar"}
                                elementId={i%2==0?"dot-"+rowCount+"-"+dotCount:"horizontal-bar-"+rowCount+"-"+horizontalCount}
                            />)
            }   
        }
        else{
            for(i=0; i<5; ++i){
                verticalCount = keyCountVertical + 1;
                grid.push(<Dot
                                key={keyCountVertical++}
                                elementName="vertical-bar"
                                elementId={"vertical-bar-"+rowCount+"-"+verticalCount}
                            />)
            }
        }
        for(i=0; i<65; ++i){
            map.set(i, "unclicked");
        }
    })()

    return (
        <>
            <div className={props.containerName} id={props.containerId}>{grid}</div>
            {console.log(map)}
        </>
    )
}
