import React from 'react'
import Dot from "./Dot"

export default function Grid() {
    var horizontalGrid = [];
    var verticalGrid = [];
    var grid = [];
    var keyCount=0;
    let i, j;
    (function makeGrid(){
        for(i = 0; i <5; ++i){
            for(j = 0; j < 5; ++j){
                horizontalGrid.push(<Dot key ={((i+1)*5)+j} orientation="horizontal-bars"/>);
                verticalGrid.push(<Dot key ={((i+1)*10)+j + 25} orientation="vertical-bars"/>);
            }
            horizontalGrid.push(<br/>);
            verticalGrid.push(<br/>);
        }
    })()

    (function createBoard(){
        for(i=0; i<5; ++i){
            if(i%2==0){
                // horizontal orientation lines
                for(j=0; j<9; ++j){
                    grid.push(<Dot key={keyCount++} orientation={j%2==0?"dot":"horizontal-bar"}/>)
                }
            }
            else{
                for(j=0; j<5; ++j){
                    grid.push(<Dot key={((i)*5)+j+1} orientation={j%2==0?"dot":"horizontal-bar"}/>)
                }
            }
        }
    })()
    return (
        <>
            <div className="horizontal-grid">{horizontalGrid}</div>
            <div className="vertical-grid">{verticalGrid}</div>
        </>
    )
}
