import React, {useState} from 'react';
import "./style.css"
import {io} from "socket.io-client"
import Game from "./Game"
import Header from './Components/Header';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// import randomstring from 'randomstring';

const socket = io('http://localhost:4000');

export default function (props) {
    const [roomId, setRoomId] = useState("");
    const [shared, setShared] = useState(false);
    const [joined, setJoined] = useState(false)
    const [roomFull, setRoomFull] = useState(false);
    const [playerRole, setPlayerRole] = useState(); // playerRole=1 if he created the room, else 2 if he joined one
    const [roomCode, setRoomCode] = useState();

    socket.on("start-game", ()=>{
        setJoined(true);
    })
    socket.on("room full", ()=>{
        setRoomFull(true);
        console.log(roomFull);
    })

    function idCopied (){
        toast("Room ID copied to clipboard", {
            position: "top-right",
            autoClose: 1000,
            hideProgressBar: true,
            closeOnClick: true,
            pauseOnHover: false,
            draggable: true,
            progress: undefined,
        });  
    } 

    const generateRandomString = (myLength) => {
        const chars =
          "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
        const randomArray = Array.from(
          { length: myLength },
          (v, k) => chars[Math.floor(Math.random() * chars.length)]
        );

        const randomString = randomArray.join("");
        return randomString;
      };

    return (
        <>
        <Header gridSize={props.gridSize}/>
        {joined?
        <Game gridSize={props.gridSize} roomId={socket.id} playerRole={playerRole}/>:
        !shared?
        <div className="rooms-main-container">
            <div className="create-room-container">
                <button 
                    className="create-room" 
                    onClick={()=>{
                        idCopied();
                        setShared(true);
                        setRoomCode(generateRandomString(6));
                        console.log(roomCode);
                        socket.emit("room-created", roomCode);
                        setPlayerRole(1)
                        navigator.clipboard.writeText(roomCode)
                        }
                    }>    
                </button>
            </div>
            <div className="join-room-container">
                <input 
                    className="room-input" 
                    type="text" 
                    style={roomId!==""?{marginRight: "10px"}:{marginRight:"0px"}}
                    onChange={(e)=>{
                        setRoomId(e.target.value);
                    }} 
                    onKeyPress={(e)=>{
                        if(e.key==="Enter"){
                            setPlayerRole(2)
                            socket.emit("joined-room", roomId);
                        }
                    }}
                />
                {roomId!==""?
                    <button className="join-room"   
                        onClick={()=>{
                            setPlayerRole(2);
                            socket.emit("joined-room", roomId)
                        }}>
                    Join
                    </button>
                :console.log("Enter Room Id")
                }
            </div>
            {!roomFull?
                <p className="room-id-statement">Share Room ID with a friend or enter a Room ID to start the game</p>
            :
                <p className="room-full-statement">Room is already full</p>
            }
        </div>:
        <div className="rooms-main-container waiting-room-container">
            <div className="waiting-icon-container">
            </div>
            {console.log(roomId)}
            <div className="room-id-container" onClick={idCopied}>
                <span>{roomCode}</span>
            </div>
            <p className="room-id-statement">Waiting for the other player to join the room</p>
        </div>}
        <ToastContainer/>
    </>
  )
}
