import React, {useState} from 'react'
import "../style.css"
import {Route, Link, Routes} from "react-router-dom";
import Game from "../Game.js"

export default function GameOver(props) {
    var result;
    const [gridSize, setGridSize] = useState(4);
    var message="";

    (function calcResult(){
        if(props.redScore>props.blueScore){
            result="red";
        }
        else if(props.redScore<props.blueScore){
            result="blue";
        }
        else{
            result="draw";
        }
    })();

    function createEmoji(){
        var i=0;
        for (const [key, value] of props.boxMap.entries()) {
            if(props.gridSize==4){
                if(i==3){
                    message+="\n";
                    i=0;
                }
            }
            else{
                if(i==7){
                    message+="\n";
                    i=0;
                }
            }
            if(value=="#c5183b"){
                message+="ðŸŸ¥";
            }
            else{
                message+="ðŸŸ¦";
            }
            i++;
        }   
        console.log(message);
    }

    return (
    <>
        <div className="logo-container"></div>
        <div className="winner-container" id={result+"-winner-container"} onClick={()=>{createEmoji();}}>
            <h2 className="winner-text" >
                {result=="draw"?"It's a draw":result+" wins"}
            </h2>
        </div>
        <button className="grid-button" id={props.gridSize==4?"four-button":"eight-button"} onClick={()=>{window.location.reload(false)}}>Go Again</button>
    </>
  )
}
