import './App.css';
import react, {useState} from 'react';
import "./style.css";
import Game from './Game';

function App() {
  const [gameStart, setGameStart] = useState(false);
  const [gridSize, setGridSize] = useState(4);
  return(
    <>
      <div className="home-container">
        <h1 className="title">Dot Game</h1>
        {gameStart?<Game gridSize={gridSize}/>:
          <>
            <button className="buton" onClick={()=>{setGridSize(4); setGameStart(true);}}>4X4</button>
            <button onClick={()=>{setGridSize(8); setGameStart(true);}}>8X8</button>
          </>}
      </div>
    </>
  )
}

export default App;
