import React, {useState} from 'react'

export default function () {
var roomId;
const [shared, setShared] = useState(false);
  return (
    <div className="rooms-main-container">
        <div className="create-room-container">
            <button className="create-room" onClick={()=>{window.alert("Room ID copied to clipboard."); shared=true;}}></button>
        </div>
        <div className="join-room-container">
            <input className="room-input" type="text" value={roomId}/>
            <button className="join-room">Join</button>
        </div>
        {shared?<p className="room-id-statement">Waiting for the other player to join the room</p>:<p className="room-id-statement">Share Room ID with a friend or enter a Room ID to start the game</p>}
    </div>
  )
}
