import './App.css';
import react, {useState} from 'react';
import "./style.css";
import Game from './Game';
import {Route, Link, Routes} from "react-router-dom";
import {io} from "socket.io-client"

const socket = io('http://localhost:4000');

function App() {
  const [gridSize, setGridSize] = useState(4);
  const [chosenMode, setChosenMode] = useState("")
  (()=>{
    socket.emit("onload-message", "hello")
  })()
  return(
    <>
      <Routes>
              <Route exact path="" className="home-container" element={
                <div className="home-container">
                  <div className="logo-container"></div>
                  <p className="rule">Own the most boxes at the end of the game to win</p>
                  <div className="grid-buttons-container">
                    {/* <Link to="/game" style={{textDecoration: "none"}}> */}
                      <button className="grid-button" id={"four-button-"+chosenMode} onClick={()=>{setGridSize(4); setChosenMode("four")}}>4X4</button>
                    {/* </Link> */}
                    {/* <Link to="/game" style={{textDecoration: "none"}}> */}
                      <button className="grid-button" id="eight-button" onClick={()=>{setGridSize(8);}}>8X8</button>
                    {/* </Link> */}
                  </div>
                </div>
              }/>   
              <Route exact path="/game" element={<Game gridSize={gridSize}/>}/>
      </Routes>
      {/* <Footer/> */}
    </>
  )
}

export default App;
