import React, {useState} from 'react'
import "../style.css"
import {Route, Link, Routes} from "react-router-dom";
import Game from "../Game.js"

export default function GameOver(props) {
    var result;
    const [gridSize, setGridSize] = useState(4);

    (function calcResult(){
        if(props.redScore>props.blueScore){
            result="red";
        }
        else if(props.redScore<props.blueScore){
            result="blue";
        }
        else{
            result="draw";
        }
    })();

    return (
    <>
        <div className="logo-container"></div>
        <div className="winner-container" id={result+"-winner-container"}>
            <h2 className="winner-text" >
                {result=="draw"?"It's a draw":result+" wins"}
            </h2>
        </div>
        <button className="grid-button" id={props.gridSize==4?"four-button":"eight-button"} onClick={()=>{refreshPage;}}>Go Again</button>
    </>
  )
}
