import React, {useState} from 'react'
import "../style.css"
import {Route, Link, Routes} from "react-router-dom";
import Game from "../Game.js"

export default function GameOver(props) {
    var result;
    const [gridSize, setGridSize] = useState(4);

    (function calcResult(){
        if(props.redScore>props.blueScore){
            result="red";
        }
        else if(props.redScore<props.blueScore){
            result="blue";
        }
        else{
            result="draw";
        }
    })();

    return (
    <>
      <Routes>
        <div className="logo-container"></div>
        <div className="winner-container" id={result+"-winner-container"}>
            <h2 className="winner-text" >
                {result=="draw"?"It's a draw":result+" wins"}
            </h2>
        </div>
        <Link to="/game" style={{textDecoration: "none"}}>
            <button className="grid-button" id="four-button" onClick={()=>{setGridSize(4);}}>4X4</button>
        </Link>
        <Link to="/game" style={{textDecoration: "none"}}>
            <button className="grid-button" id="eight-button" onClick={()=>{setGridSize(8);}}>8X8</button>
        </Link>
        <Route exact path="/game" element={<Game gridSize={gridSize}/>}/>
      </Routes>
    </>
  )
}
