import React from 'react'
import Dot from "./Dot"
import Container from "./Container"

export default function Grid() {
    var grid = [];
    var keyCount=0;
    let i, j;
    (function createBoard(){
        for(i=0; i<9; ++i){
            if(i%2==0){
                // horizontal orientation lines & dots
                for(j=0; j<9; ++j){
                    grid.push(<Dot 
                                    key={keyCount++} 
                                    elementName={j%2==0?"dot":"horizontal-bar"}
                                    elementId={j%2==0?"dot-"+keyCount:"horizontal-bar-"+keyCount}
                                    containerName={"horizontal-bar-container"}
                                    containerId={"horizontal-bar-container-"+i/2}
                                />)
                }
            }
            else{
                // vertical orientation lines
                for(j=0; j<5; ++j){
                    grid.push(<Dot
                                    key={keyCount++}
                                    elementName="vertical-bar"
                                    elementId={"vertical-bar-"+keyCount}
                                    containerName={"vertical-bar-container"}
                                    containerId={"vertical-bar-container-"+Math.ceil(i/2)}
                                />)
                }
            }
            grid.push(<br/>);
        }
    })()
    return (
        <>
            <div className="grid">{grid}</div>
        </>
    )
}
