import React, {useState} from 'react'
import ReactDOM from 'react-dom';
import LineTo from "react-lineto"
import "../style.css";

export default function Dot(props) {
    const [d1, setd1] = useState(0);
    const [d2, setd2] = useState(0);
    const [d3, setd3] = useState(0);
    const [d4, setd4] = useState(0);
    const [turn, setTurn] = useState(0);
    const [prevClick, setPrevClick] = useState(null);
    const [dot1, setDot1] = useState(null);
    const [dot2, setDot2] = useState(null);
    const [cl, setCl] = useState("col");
    function whichDot(dotIndex){
        console.log("Current Click: " + dotIndex);
        console.log("Previous click: " + prevClick);
        setd1(dotIndex-1);
        setd2(dotIndex+1);
        setd3(dotIndex-5);
        setd4(dotIndex+5);
        if(dotIndex%5==0){
            setd2(0);
        }
        if(dotIndex%5==1){
            setd1(0);
        }
        setPrevClick(dotIndex);
        if(turn==0){
            setTurn(1);
        }
        if(turn==1){
            setTurn(0);
            setPrevClick(null);
        }
        if(prevClick == d1 || prevClick==d2 || prevClick==d3 || prevClick==d4){
            setDot1(prevClick);
            setDot2(dotIndex);
        }
    }
    return (
    <>
        <div className="row" id="row-1">
          <div className={dot1==1?"col A":dot2==1?"col B":"col"} id={(d1==1||d2==1||d3==1||d4==1)?"highlight":"row-1-col-1"} onClick={()=>{whichDot(1)}}></div>
          <div className={dot1==2?"col A":dot2==2?"col B":"col"} id={(d1==2||d2==2||d3==2||d4==2)?"highlight":"row-1-col-2"} onClick={()=>{whichDot(2)}}></div>
          <div className={dot1==3?"col A":dot2==3?"col B":"col"} id={(d1==3||d2==3||d3==3||d4==3)?"highlight":"row-1-col-3"} onClick={()=>{whichDot(3)}}></div>
          <div className={dot1==4?"col A":dot2==4?"col B":"col"} id={(d1==4||d2==4||d3==4||d4==4)?"highlight":"row-1-col-4"} onClick={()=>{whichDot(4)}}></div>
          <div className={dot1==5?"col A":dot2==5?"col B":"col"} id={(d1==5||d2==5||d3==5||d4==5)?"highlight":"row-1-col-5"} onClick={()=>{whichDot(5)}}></div>
        </div>
        <div className="row" id="row-2">
          <div className={dot1==6?"col A":dot2==6?"col B":"col"} id={(d1==6||d2==6||d3==6||d4==6)?"highlight":"row-2-col-1"} onClick={()=>{whichDot(6)}}></div>
          <div className={dot1==7?"col A":dot2==7?"col B":"col"} id={(d1==7||d2==7||d3==7||d4==7)?"highlight":"row-2-col-2"} onClick={()=>{whichDot(7)}}></div>
          <div className={dot1==8?"col A":dot2==8?"col B":"col"} id={(d1==8||d2==8||d3==8||d4==8)?"highlight":"row-2-col-3"} onClick={()=>{whichDot(8)}}></div>
          <div className={dot1==9?"col A":dot2==9?"col B":"col"} id={(d1==9||d2==9||d3==9||d4==9)?"highlight":"row-2-col-4"} onClick={()=>{whichDot(9)}}></div>
          <div className={dot1==10?"col A":dot2==10?"col B":"col"} id={(d1==10||d2==10||d3==10||d4==10)?"highlight":"row-2-col-5"} onClick={()=>{whichDot(10)}}></div>
        </div>
        <div className="row" id="row-3">
          <div className={dot1==11?"col A":dot2==11?"col B":"col"} id={(d1==11||d2==11||d3==11||d4==11)?"highlight":"row-3-col-1"} onClick={()=>{whichDot(11)}}></div>
          <div className={dot1==12?"col A":dot2==12?"col B":"col"} id={(d1==12||d2==12||d3==12||d4==12)?"highlight":"row-3-col-2"} onClick={()=>{whichDot(12)}}></div>
          <div className={dot1==13?"col A":dot2==13?"col B":"col"} id={(d1==13||d2==13||d3==13||d4==13)?"highlight":"row-3-col-3"} onClick={()=>{whichDot(13)}}></div>
          <div className={dot1==14?"col A":dot2==14?"col B":"col"} id={(d1==14||d2==14||d3==14||d4==14)?"highlight":"row-3-col-4"} onClick={()=>{whichDot(14)}}></div>
          <div className={dot1==15?"col A":dot2==15?"col B":"col"} id={(d1==15||d2==15||d3==15||d4==15)?"highlight":"row-3-col-5"} onClick={()=>{whichDot(15)}}></div>
        </div>
        <div className="row" id="row-4">
          <div className={dot1==16?"col A":dot2==16?"col B":"col"} id={(d1==16||d2==16||d3==16||d4==16)?"highlight":"row-4-col-1"} onClick={()=>{whichDot(16)}}></div>
          <div className={dot1==17?"col A":dot2==17?"col B":"col"} id={(d1==17||d2==17||d3==17||d4==17)?"highlight":"row-4-col-2"} onClick={()=>{whichDot(17)}}></div>
          <div className={dot1==18?"col A":dot2==18?"col B":"col"} id={(d1==18||d2==18||d3==18||d4==18)?"highlight":"row-4-col-3"} onClick={()=>{whichDot(18)}}></div>
          <div className={dot1==19?"col A":dot2==19?"col B":"col"} id={(d1==19||d2==19||d3==19||d4==19)?"highlight":"row-4-col-4"} onClick={()=>{whichDot(19)}}></div>
          <div className={dot1==20?"col A":dot2==20?"col B":"col"} id={(d1==20||d2==20||d3==20||d4==20)?"highlight":"row-4-col-5"} onClick={()=>{whichDot(20)}}></div>
        </div>
        <div className="row" id="row-5">
          <div className={dot1==21?"col A":dot2==21?"col B":"col"} id={(d1==21||d2==21||d3==21||d4==21)?"highlight":"row-5-col-1"} onClick={()=>{whichDot(21)}}></div>
          <div className={dot1==22?"col A":dot2==22?"col B":"col"} id={(d1==22||d2==22||d3==22||d4==22)?"highlight":"row-5-col-2"} onClick={()=>{whichDot(22)}}></div>
          <div className={dot1==23?"col A":dot2==23?"col B":"col"} id={(d1==23||d2==23||d3==23||d4==23)?"highlight":"row-5-col-3"} onClick={()=>{whichDot(23)}}></div>
          <div className={dot1==24?"col A":dot2==24?"col B":"col"} id={(d1==24||d2==24||d3==24||d4==24)?"highlight":"row-5-col-4"} onClick={()=>{whichDot(24)}}></div>
          <div className={dot1==25?"col A":dot2==25?"col B":"col"} id={(d1==25||d2==25||d3==25||d4==25)?"highlight":"row-5-col-5"} onClick={()=>{whichDot(25)}}></div>
        </div>
        <div className="row" id="row-6">
          <div className={dot1==26?"col A":dot2==26?"col B":"col"} id={(d1==26||d2==26||d3==26||d4==26)?"highlight":"row-6-col-1"} onClick={()=>{whichDot(26)}}></div>
