import './App.css';
import react, {useState} from 'react';
import "./style.css";
import Game from './Game';
import {Route, Link, Routes} from "react-router-dom";

function App() {
  const [gridSize, setGridSize] = useState(4);
  return(
    <>
      <div className="home-container">
        <div className="logo-container"></div>
        <Link to={"/"+gridSize+"X"+gridSize} style={{textDecoration: "none"}}>
          <button className="button" onClick={()=>{setGridSize(4);}}>4X4</button>
        </Link>
        <Link to={"/"+gridSize+"X"+gridSize} style={{textDecoration: "none"}}>
          <button onClick={()=>{setGridSize(8);}}>8X8</button>
        </Link>    
        <Routes>
                <Route exact path={"/"+gridSize+"X"+gridSize} element={<Game gridSize={gridSize}/>}/>
        </Routes>
      </div>
    </>
  )
}

export default App;
