import React, {useState, useEffect, useRef} from 'react'
import "../style.css";
import {io} from "socket.io-client";

const socket = io('http://localhost:4000');

export default function Chat(props) {
    const [message, setMessage]= useState("");
    // var chatColor = props.chatColor=="#c5183b"?"rgba(195, 24, 58, 0.5)":"rgba(59, 146, 155, 0.5)";
    var chatColor = props.chatColor=="#c5183b"?"#c5183b":"#3b919b";
    const anchorDiv = useRef();

    useEffect(()=>{
        anchorDiv.current.scrollIntoView({behaviour: 'smooth'});
    }, [props.messageArr])

    return (
    <>
        <div className="chat-outer-container">
            <div className="chat-role-container" style={{color: props.chatColor}}>
                Chat as {chatColor=="#c5183b"?"Red":"Blue"}
            </div>
            <div className="chat-main-container">
                {props.messageArr.map((msg, index)=>{
                    return(
                        <div 
                            className="chat-message-container" 
                            key={index}
                        >
                                <p 
                                    className="chat-message-text"
                                    style={{color: msg.sender==1?"#c5183b":"#3b919b"}}
                                >
                                        {msg.message}
                                </p>
                        </div>
                    )
                })}
                <div ref={anchorDiv}></div>
            </div>
            <div className="chat-input-container">
                <div className="pretyped-messages-container">
                    <div className="emoji-1">
                        <span role="img" aria-label="emoji">😂</span>
                    </div>
                    <div className="emoji-2">
                        <span role="img" aria-label="emoji">❤️</span>
                    </div>
                </div>
                <input 
                    type="text" 
                    className="chat-input"
                    placeholder="Type a message..."
                    values={message}
                    onKeyPress={(e)=>{
                        if(e.key==="Enter"){
                            props.sendMessage(e.target.value, props.playerRole);
                            e.target.value="";
                        }
                    }}
                    onChange={(e)=>{
                        setMessage(e.target.value);
                    }}
                />
                <button
                    className="chat-send-button"
                    onClick={()=>{
                        props.sendMessage(message, props.playerRole);
                        setMessage("");
                    }
                    }
                >
                    Send
                </button>
            </div>
        </div>
    </>
  )
}
