import logo from './logo.svg';
import './App.css';
import react, {useState} from 'react';
import "./style.css";
import Dot from './Components/Dot';
import Grid from './Components/Grid';

function App() {
  var i;
  const [elMap, setMap] = useState(new Map());
  const [turn, setTurn] = useState("#c5183b");
  const [flag, setFlag] = useState(true);

  (function initMap(){
    if(flag){
      for(i=0; i<65; ++i){
        elMap.set(i, "unclicked");
      }
      setFlag(false);
    }
  })()

  function elementCheck(id){
    console.log("Clicked element is " + id);
      var position = calcPosition(id);
    setMap(elMap.set(position, "clicked"));
    console.log(position);
      colorBars();
    if(id.charAt(0) == "h"){
      checkVerticalBoxes(id, position);
    }
    else{
      checkHorizontalBoxes(id, position);
    }
  }

  function calcPosition(id){
    var part1=0, part2=0, position=0;
    if(id.charAt(0)=="d"){
      part1 = 14*(parseInt(id.charAt(2))-1);
      part2 = 2*(parseInt(id.charAt(4)))-2;
    }
    else if(id.charAt(0)=="h"){
      part1 = 14*(parseInt(id.charAt(2))-1);
      part2 = 2*(parseInt(id.charAt(4)))-1;
    }
    else{
      part1 = 14*(parseInt(id.charAt(2)))-5;
      part2 = parseInt(id.charAt(4));
    }
    position = part1 + part2;
    return position;
  }

  function colorBars(){
    setTurn(turn=="#c5183b"?"#3b919b":"#c5183b");
  }

  function checkVerticalBoxes(id, position){
    var id1, id2, id3, id4, id5, id6;
    var pos1, pos2, pos3, pos4, pos5, pos6;
    var n = parseInt(id.charAt(2)), m = parseInt(id.charAt(4));
    id1="h-"+(n-1)+"-"+m;
    id2="h-"+(n+1)+"-"+m;
    id3="v-"+(n-1)+"-"+m;
    id4="v-"+(n-1)+"-"+(m+1);
    id5="v-"+(n)+"-"+m;
    id6="v-"+(n)+"-"+(m+1);
    pos1=calcPosition(id1);
    pos2=calcPosition(id2);
    pos3=calcPosition(id3);
    pos4=calcPosition(id4);
    pos5=calcPosition(id5);
    pos6=calcPosition(id6);

    if(elMap.get(pos1)=="clicked" && elMap.get(pos3)== "clicked" && elMap.get(pos4)=="clicked"){
      console.log("upper box filled");
    }
    else if(elMap.get(pos2)== "clicked" && elMap.get(pos5)=="clicked" && elMap.get(pos6)== "clicked"){
      console.log("lower box filled");
    }
    else if(elMap.get(pos1)=="clicked" && elMap.get(pos2)== "clicked" && elMap.get(pos3)=="clicked" && elMap.get(pos4)== "clicked" && elMap.get(pos5)=="clicked" && elMap.get(pos6)== "clicked"){
      console.log("upper and lower boxes filled");
    }
  }

  function checkHorizontalBoxes(id, position){
    // checking for horizontal boxes
    if(elMap.get(position-9)=="clicked" && elMap.get(position+5)== "clicked" && elMap.get(position-1)=="clicked"){
      console.log("left box filled");
    }
    else if(elMap.get(position+1)== "clicked" && elMap.get(position-7)=="clicked" && elMap.get(position+7)== "clicked"){
      console.log("right box filled");
    }
    else if(elMap.get(position-9)=="clicked" && elMap.get(position-7)== "clicked" && elMap.get(position-1)=="clicked" && elMap.get(position+1)== "clicked" && elMap.get(position+5)=="clicked" && elMap.get(position+7)== "clicked"){
      console.log("left and right boxes filled");
    }
  }

  return (
    <>
      <div className="center-container">
        <h1 className="title">Dot Game</h1>
        <Grid func={elementCheck} turn={turn} map={elMap}/>
      </div>
    </>
  );
}

export default App;
