import React from 'react'
import "../style.css";

export default function Chat(props) {
  return (
    <>
        <div className="chat-main-container">
            {props.messageArr.map((message, index)=>{
                return(
                    <div className="chat-message-container" key={index}>
                        <p className="chat-message-text">{message}</p>
                    </div>
                )
            })}
            <div className="chat-input-container">
                <input 
                    type="text" 
                    className="chat-input"
                    placeholder="Type a message..."
                    onKeyPress={(e)=>{
                        if(e.key==="Enter"){
                            props.sendMessage(e.target.value);
                            e.target.value="";
                        }
                    }
                    }
                />
                <button
                    className="chat-send-button"
                    onClick={()=>{
                        props.sendMessage(document.getElementsByClassName("chat-input")[0].value);
                        document.getElementsByClassName("chat-input")[0].value="";
                    }
                    }
                >
                    Send
                </button>
            </div>
        </div>
    </>
  )
}
