import React, {useState, useEffect} from 'react'
import "../style.css"
import {Route, Link, Routes, useNavigate} from "react-router-dom";
import Game from "../Game.js"
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Confetti from 'react-confetti'

export default function GameOver(props) {
    var result;
    const [gridSize, setGridSize] = useState(4);
    const [hideWinner, setHideWinner] = useState("flex");
    const [showShare, setShowShare] = useState("none");
    const [showConfetti, setShowConfetti] = useState(false);
    const navigate = useNavigate();
    const generateRandomString = (myLength) => {
        const chars =
          "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
        const randomArray = Array.from(
          { length: myLength },
          (v, k) => chars[Math.floor(Math.random() * chars.length)]
        );

        const randomString = randomArray.join("");
        return randomString;
    };
    const newRoomId = generateRandomString(6);

    (function calcResult(){
        if(props.redScore>props.blueScore){
            result = "red";
        }
        else if(props.redScore<props.blueScore){
            result = "blue";
        }
        else{
            result = "draw";
        }
    })();

    function createEmoji(){
        var i=0;
        if(result == "draw"){
            var message = "It's a draw\n\n";
        }
        else if(result=="red"){
            message = "ðŸ‘‘ Red\n\n";
        }
        else{
            message = "ðŸ‘‘ Blue\n\n";
        }
        for (const [key, value] of props.boxMap.entries()) {
            if(props.gridSize==4){
                if(i==4){
                    message+="\n";
                    i=0;
                }
            }
            else{
                if(i==8){
                    message+="\n";
                    i=0;
                }
            }
            if(value=="#c5183b"){
                message+="ðŸŸ¥";
            }
            else{
                message+="ðŸŸ¦";
            }
            i++;
        }   
        console.log(message);
        navigator.clipboard.writeText(message);
    }

    function resultCopied (){
        toast("Result copied to clipboard", {
            position: "top-right",
            autoClose: 1000,
            hideProgressBar: true,
            closeOnClick: true,
            pauseOnHover: false,
            draggable: true,
            progress: undefined,
        });  
    } 

    useEffect(()=>{
        setShowConfetti(true);
    }, [])

    return (
    <>
        {showConfetti && 
            <Confetti 
                colors = {result=="blue"?["#3b919b"]:["#c5183b"]}
                numberOfPieces = {80}
                recycle = {true}
                // initialVelocityX = {10}
                // initialVelocityY = {10}
                // tweenDuration = {1000}
            />
        } 
        <div className="logo-container"></div>
        <div className="winner-outer-container">
            <div 
                className="winner-container" 
                id={result+"-winner-container"} 
                onMouseEnter={()=>{setHideWinner("none"); setShowShare("flex")}} 
                onMouseLeave={()=>{setHideWinner("flex"); setShowShare("none")}}
                onClick={()=>{
                    resultCopied();
                    createEmoji();
                }}
            >
                    <div 
                        className="winner-text-container" 
                        style={{display: hideWinner}}
                    >
                            <h2 
                                className="winner-text">
                                    {result=="draw"?"It's a draw":result+" wins"}
                            </h2>
                    </div>
                    <div 
                        className="share-container" 
                        style={{display: showShare}} 
                    >
                </div>
            </div>
        </div>
        <ToastContainer/>
        {/* <Link to={"/game"}> */}
            <button 
                className="grid-button" 
                id={props.gridSize==4?"four-button":"eight-button"} 
                onClick={()=>{navigate(-1)}}
                >
                Go Again
            </button>
        {/* </Link> */}
        {/* {console.log(props.gridSize, props.vsComp)} */}
        {/* <Routes>
            <Route exact path={"/game"} element={<Game gridSize={props.gridSize} vsComp={props.vsComp}/>}/>
        </Routes> */}
    </>
  )
}
